["custom"] = "(function()\n    local baseState = {\n      show = true,\n      progressType = \"static\",\n      value = 0,\n      total = 1,\n    }\n    local function mutate(states, field, value)\n      if not states[\"\"] then states[\"\"] = CreateFromMixins(baseState) end\n      local mutated = states[\"\"][field] ~= value\n      if mutated then states[\"\"].changed = true end\n      states[\"\"][field] = value\n      return mutated\n    end\n    \n    return function(states, event, _, otherStates)\n      local mutated = mutate(states, \"value\", otherStates and otherStates[\"\"] and otherStates[\"\"][\"points\"] and otherStates[\"\"][\"points\"][1] or 0)\n      mutated = mutate(states, \"total\", UnitHealthMax(\"player\")) or mutated\n      if mutated then mutate(states, \"percentstaggertick\", (states[\"\"].total ~= 0 and (states[\"\"].value / states[\"\"].total) * 10000) or nil) end\n      return mutated\n    end\nend)()",
