of the Triumvirate\n            [209] = {}, -- The Arcway\n            [207] = {}, -- Vault of the Wardens\n            \n            -- Battle for Azeroth\n            [244] = {424187}, -- Atal'Dazar\n            [245] = {410071}, -- Freehold\n            [249] = {}, -- King's Rest\n            [369] = {373274}, -- Operation: Mechagon - Junkyard\n            [370] = {373274}, -- Operation: Mechagon - Workshop\n            [252] = {}, -- Shrine of the Storm\n            [353] = {445418, 464256}, -- Siege of Boralus\n            [250] = {}, -- Temple of Sethraliss\n            [247] = {}, -- The MOTHERLODE!!!\n            [251] = {410074}, -- The Underrot\n            [246] = {}, -- Tol Dagor\n            [248] = {424167}, -- Waycrest Manor\n            \n            -- Shadowlands\n            [377] = {354468}, -- De Other Side\n            [378] = {354465}, -- Halls of Atonement\n            [375] = {354464}, -- Mists of Tirna Scithe\n            [379] = {354463}, -- Plaguefall\n            [380] = {354469}, -- Sanguine Depths\n            [381] = {354466}, -- Spires of Ascension\n            [392] = {367416}, -- Tazavesh: So'leah's Gambit\n            [391] = {367416}, -- Tazavesh: Streets of Wonder\n            [376] = {354462}, -- The Necrotic Wake\n            [382] = {354467}, -- Theater of Pain\n            \n            -- Dragonflight\n            [402] = {393273}, -- Algeth'ar Academy\n            [405] = {393267}, -- Brackenhide Hollow\n            [463] = {424197}, -- Dawn of the Infinite: Galakrond's Fall\n            [464] = {424197}, -- Dawn of the Infinite: Murozond's Rise\n            [406] = {393283}, -- Halls of Infusion\n            [404] = {393276}, -- Neltharus\n            [399] = {393256}, -- Ruby Life Pools\n            [401] = {393279}, -- The Azure Vault\n            [400] = {393262}, -- The Nokhud Offensive\n            [403] = {393222}, -- Uldaman: Legacy of Tyr\n            \n            -- The War Within\n            [503] = {445417}, -- Ara-Kara, City of Echoes\n            [506] = {445440}, -- Cinderbrew Meadery\n            [502] = {445416}, -- City of Threads\n            [504] = {445441}, -- Darkflame Cleft\n            [499] = {445444}, -- Priory of the Sacred Flame\n            [505] = {445414}, -- The Dawnbreaker\n            [500] = {445443}, -- The Rookery\n            [501] = {445269}, -- The Stonevault\n        }, {__index = function () return {} end})\n    \n    Library.Name = aura_env.id\n    \n    Library.OutdatedText = \"Dungeon or spell data is missing. Please check Wago.io for updates.\"\n    \n    Library.SpellStatus = {\n        Ready = 1,\n        OnCooldown = 2,\n        NotLearned = 3,\n    }\n    \n    function Library:Initialize()\n        if not C_AddOns.IsAddOnLoaded(\"Blizzard_ChallengesUI\") then\n            self:RegisterEvent(\"ADDON_LOADED\")\n            return\n        end\n        \n        WeakAuras.WatchGCD()\n        \n        if ChallengesFrame and type(ChallengesFrame.Update) == \"function\" then\n            hooksecurefunc(ChallengesFrame, \"Update\", function () Library:CreateAllDungeonButtons() end)\n        end\n        \n        Library:CreateAllDungeonButtons()\n    end\n    \n    function Library:OnEvent(event, arg1)\n        if event == \"ADDON_LOADED\" then\n            if arg1 == \"Blizzard_ChallengesUI\" then\n                self:Initialize()\n                self:UnregisterEvent(\"ADDON_LOADED\")\n            end\n        end\n    end\n    \n    function Library:CreateAllDungeonButtons()\n        if (InCombatLockdown()) then return end\n        if (not ChallengesFrame) then return end\n        if (not ChallengesFrame.DungeonIcons) then return end\n        \n        for _, dungeonIcon in next, ChallengesFrame.DungeonIcons do\n            self:CreateDungeonButton(dungeonIcon, self.MapIdToSpellId[dungeonIcon.mapID])\n        end\n    end\n    \n    function Library:CreateDungeonButton(parent, spellIDs)\n        local button = self[parent] or CreateFrame(\"Button\", nil, parent, \"InsecureActionButtonTemplate\")\n        button:SetAllPoints(parent)\n        button:RegisterForClicks(\"AnyDown\", \"AnyUp\")\n        button:SetAttribute(\"type\", \"spell\")\n        button:SetAttribute(\"spell\", nil)\n        button:SetScript(\"OnEnter\", function () self:UpdateDungeonButton(parent, button, spellIDs, true) end)\n        button:SetScript(\"OnLeave\", function () if GameTooltip:IsOwned(parent) then GameTooltip:Hide() end end)\n        self[parent] = button\n    end\n    \n    function Library:UpdateDungeonButton(parent, button, spellIDs, initialize)\n        if (WeakAuras.InLoadingScreen()) then return end\n        if (not initialize and not GameTooltip:IsOwned(parent)) then return end\n        local Parent_OnEnter = parent:GetScript(\"OnEnter\")\n        if (not Parent_OnEnter) then return end\n        local teleportSpell = self:GetBestTeleportSpellInfo(spellIDs)\n        \n        Parent_OnEnter(parent)\n        \n        if GameTooltip:NumLines() > 0 then\n            GameTooltip:AddLine(\" \")\n        end\n        \n        if not teleportSpell then\n            GameTooltip:AddLine(self.Name)\n            GameTooltip:AddLine(self.OutdatedText, 1, 0, 0)\n        elseif teleportSpell.status == self.SpellStatus.NotLearned then\n            GameTooltip:AddLine(teleportSpell.name)\n            GameTooltip:AddLine(SPELL_FAILED_NOT_KNOWN, 1, 0, 0)\n        elseif teleportSpell.status == self.SpellStatus.OnCooldown then\n            GameTooltip:AddLine(teleportSpell.name)\n            GameTooltip:AddLine(teleportSpell.remainingTime, 1, 0, 0)\n        elseif teleportSpell.status == self.SpellStatus.Ready then\n            GameTooltip:AddLine(teleportSpell.name)\n            GameTooltip:AddLine(READY, 0, 1, 0)\n        end\n        \n        GameTooltip:Show()\n        button:SetAttribute(\"spell\", teleportSpell and teleportSpell.id)\n        C_Timer.After(1, function () self:UpdateDungeonButton(parent, button, spellIDs) end)\n    end\n    \n    function Library:GetBestTeleportSpellInfo(spellIDs)\n        local teleportSpells = {}\n        \n        for spellIndex, spellID in next, spellIDs do\n            table.insert(teleportSpells, self:GetTeleportSpellInfo(spellIndex, spellID))\n        end\n        \n        table.sort(teleportSpells, function(a, b)\n                if a.status == b.status then\n                    return a.index < b.index\n                else\n                    return a.status < b.status\n                end\n        end)\n        \n        return teleportSpells[1]\n    end\n    \n    function Library:GetTeleportSpellInfo(spellIndex, spellID)\n        local spell = C_Spell.GetSpellInfo(spellID)\n        local isSpellKnown = IsSpellKnown(spellID)\n        local cooldown = C_Spell.GetSpellCooldown(spellID)\n        local isOnCooldown = cooldown and cooldown.duration ~= 0 and cooldown.duration ~= WeakAuras.gcdDuration() or false\n        local remainingTime = isOnCooldown and SecondsToTime(ceil(cooldown.startTime + cooldown.duration - GetTime())) or nil\n        return {\n            index = spellIndex,\n            id = spellID,\n            status = (not spell or not isSpellKnown or not cooldown) and self.SpellStatus.NotLearned or isOnCooldown and self.SpellStatus.OnCooldown or self.SpellStatus.Ready,\n            name = spell and spell.name or TELEPORT_TO_DUNGEON,\n            remainingTime = remainingTime,\n        }\n    end\n    \n    Library:SetScript(\"OnEvent\", function (self, ...) self:OnEvent(...) end)\nend\n\n-- Initialize\n\nLibrary:Initialize()",
