["custom"] = "function(event, arg1)\n    if event == \"PLAYER_TOTEM_UPDATE\" and arg1 then\n        local haveTotem, name, startTime, duration, icon = GetTotemInfo(arg1)\n        \n        if haveTotem and icon == aura_env.texture then\n            aura_env.haveTotem, aura_env.name, aura_env.startTime, aura_env.duration, aura_env.icon = haveTotem, name, startTime, duration, icon\n            aura_env.index = arg1\n            \n            return true\n            \n        elseif aura_env.index == arg1 then\n            aura_env.index = 0\n            aura_env.startTime, aura_env.duration = nil, nil\n        end\n    elseif event == \"UNIT_PET\" and \"player\" == arg1 then\n        aura_env.petExists = UnitExists(\"pet\") and not UnitIsDead(\"pet\")\n        \n        if aura_env.petExists and select(6, strsplit(\"-\", UnitGUID(\"pet\"))) == aura_env.primalGUID then\n            if not aura_env.startTime then\n                aura_env.duration = max(30, tonumber((strmatch(GetSpellDescription(aura_env.spellId), \"%d+[,.%d]*\") or \"\"):gsub(\",\", \".\") or 30))\n                aura_env.startTime = GetTime()\n            end\n            \n            return true\n        else\n            aura_env.startTime, aura_env.duration = nil, nil\n        end\n    end\nend",
