["custom"] = "aura_env.spinning = false\naura_env.progress = 0\naura_env.count = 0\naura_env.winningIndex = 0\n\naura_env.getFakeData = function () \n    local keys = {}\n    keys[1] = aura_env.createKey(1, \"Psyder\", \"Algeth'ar Academy\", 25)\n    keys[2] = aura_env.createKey(2, \"Speef\", \"The Azure Vault\", 21)\n    keys[3] = aura_env.createKey(3, \"Mop\", \"Atal'Dazar\", 13)\n    keys[4] = aura_env.createKey(4, \"Grill\", \"Dawn of the Infinite: Galakrond's Fall\", 8)\n    keys[5] = aura_env.createKey(5, \"Psyala\", \"Throne of the Tides\", 4)\n    return keys\nend\n\naura_env.getStates = function(allStates, keys)\n    for i, key in pairs(allStates) do\n        allStates[i] = nil\n    end\n    for i, key in pairs(keys) do\n        allStates[i] = key\n    end\n    \n    aura_env.count = #allStates\nend\n\naura_env.createKey = function(id, player, keystone, difficulty) \n    local keystoneAbr = aura_env.abreviateKeyName(keystone)\n    local keystoneFull = aura_env.adjustKeyName(keystone)\n    return {\n        show = true,\n        changed = true,\n        autoHide = false,\n        progressType = \"static\",\n        name = \"keystone\"..id,\n        icon = 525134,\n        index = id,\n        player = ({strsplit(\"-\", player)})[1],\n        keystone = keystoneAbr,\n        keystoneFull = keystoneFull,\n        difficulty = difficulty,\n        tooltip = keystoneFull .. \" +\" .. difficulty\n    }\nend\n\naura_env.getManualAbrvs = function() \n    return {\n        [\"Dawn of the Infinite: Galakrond's Fall\"] = \"FALL\",\n        [\"Dawn of the Infinite: Murozond's Rise\"] = \"RISE\",   \n        [\"Uldaman: Legacy of Tyr\"] = \"ULD\",\n        [\"Neltharus\"] = \"NELT\",\n        [\"Algeth'ar Academy\"] = \"AA\",\n    }\nend\n\naura_env.abreviateKeyName = function(keystone)\n    local manual = aura_env.getManualAbrvs()[keystone]\n    if manual ~= nil then return manual end\n    \n    local keyname = string.gsub(keystone, \"%'\", \" \")\n    local replaced = \"\"\n    for i, word in pairs ({strsplit(\" \",keyname)}) do \n        if i == 1 and word == \"The\" then\n            replace = replaced\n        else\n            replaced = replaced .. string.sub(word, 1, 1) \n        end\n    end\n    return string.upper(replaced)\nend\n\naura_env.adjustKeyName = function(keystone)\n    local keystoneFull = keystone\n    keystoneFull =    string.gsub(keystoneFull, \"Dawn of the Infinite\", \"DOTI\")\n    return keystoneFull\nend",
