},
["events"] = "CLEU:SPELL_SUMMON, CLEU:SPELL_AURA_APPLIED,  CLEU:SPELL_AURA_REMOVED, CLEU:SPELL_CAST_SUCCESS, PLAYER_DEAD,PLAYER_ENTERING_WORLD,CHALLENGE_MODE_START,IMP_DURATION,IMP_CLEANUP,IMP_CLEANUP_LOOP",
["subeventPrefix"] = "SPELL",
["check"] = "event",
["custom"] = "function(allstates, event, ...)\n    \n    -- Clear states when dead\n    if event == \"PLAYER_DEAD\" or event == \"CHALLENGE_MODE_START\" then\n        \n        for _, currentImp in pairs(allstates) do\n            currentImp.show = false\n            currentImp.trigger_count = false\n            currentImp.changed = true\n        end\n        \n        return true\n    end\n    \n    -- Cleanup detection loop for imp when moving around while in instances\n    if event == \"IMP_CLEANUP_LOOP\" or event == \"PLAYER_ENTERING_WORLD\" then\n        if IsInInstance() then\n            WeakAuras.ScanEvents(\"IMP_CLEANUP\")\n            \n            C_Timer.After(3,\n                function()\n                    WeakAuras.ScanEvents(\"IMP_CLEANUP_LOOP\")\n            end)\n        end\n    end\n    \n    -- Loop through the imps and removed non-existing ones\n    if event == \"IMP_CLEANUP\" then\n        \n        if not WeakAuras.IsOptionsOpen() then\n            for impGUID, currentImp in pairs(allstates) do\n                \n                local exists = CanLootUnit(impGUID)\n                if exists == nil then\n                    currentImp.show = false\n                    currentImp.trigger_count = false\n                    currentImp.changed = true\n                end\n                \n            end\n        end\n        \n        return true\n        \n    end\n    \n    -- Time based duration loop\n    if event == \"IMP_DURATION\" then\n        \n        local destGUID = select(1,...)\n        local currentImp = allstates[destGUID]\n        \n        if currentImp then\n            \n            currentImp.duration = currentImp.duration - 1\n            \n            if currentImp.duration <= 0 then\n                currentImp.show = false\n                currentImp.trigger_count = false\n            end\n            \n            currentImp.changed = true\n            \n            C_Timer.After(1,\n                function()\n                    WeakAuras.ScanEvents(\"IMP_DURATION\", destGUID)\n            end)\n        end\n        \n        return true\n    end\n    \n    -- Extract extra parameters\n    local subevent = select(2, ...)\n    local sourceGUID = select(4, ...)\n    local destGUID = select(8, ...)\n    local spellid = select(12, ...)\n    \n    -- Summon new imp\n    if subevent == \"SPELL_SUMMON\" and sourceGUID == WeakAuras.myGUID and aura_env.imps_duration[spellid] then\n        \n        allstates[destGUID] = allstates[destGUID] or {}\n        local state = allstates[destGUID]\n        \n        state.timestamp = select(1, ...)\n        \n        state.show = true\n        state.changed = true\n        state.autoHide = false\n        \n        -- Add a second to account for despawn \n        state.duration = aura_env.imps_duration[spellid]\n        state.progressType = \"static\"\n        \n        state.total = 6\n        state.value = state.total\n        state.is_boss = false\n        state.is_empowered = false\n        \n        state.trigger_count = true\n        \n        -- start duration/refresh loop\n        C_Timer.After(1,\n            function()\n                WeakAuras.ScanEvents(\"IMP_DURATION\", destGUID)\n        end)\n        \n        return true\n        \n    end\n    \n    -- Imp gang boss aura applied\n    if subevent == \"SPELL_AURA_APPLIED\" and sourceGUID == WeakAuras.myGUID and spellid == 387458 then\n        \n        local imp = allstates[destGUID]\n        \n        if imp ~= nil then\n            if not imp.is_boss then\n                aura_env.total_boss = aura_env.total_boss + 1\n                WeakAuras.ScanEvents(\"WA_TOTAL_IMPS_CHANGED\", aura_env.total_imps, aura_env.total_boss)\n            end\n            \n            imp.is_boss = true\n            imp.changed = true\n        end\n        \n        return true\n        \n    end\n    \n    -- Tyrant aura applied\n    if subevent == \"SPELL_AURA_APPLIED\" and sourceGUID == WeakAuras.myGUID and spellid == 265273 then\n        \n        local state = allstates[destGUID]\n        \n        if state ~= nil then\n            state.duration = state.duration + 15\n            state.is_empowered = true\n            state.changed = true\n        end\n        \n        return true\n        \n    end\n    \n    -- Tyrant aura removed\n    if subevent == \"SPELL_AURA_REMOVED\" and sourceGUID == WeakAuras.myGUID and spellid == 265273 then\n        \n        local state = allstates[destGUID]\n        \n        if state ~= nil then\n            state.is_empowered = false\n            state.changed = true\n        end\n        \n        return true\n        \n    end\n    \n    -- Power siphon cast\n    if subevent == \"SPELL_CAST_SUCCESS\" and sourceGUID == WeakAuras.myGUID and spellid == 264130 then\n        \n        C_Timer.After(2,\n            function()\n                WeakAuras.ScanEvents(\"IMP_CLEANUP\")\n        end)\n        \n        return true\n    end\n    \n    \n    -- Implosion cast\n    if subevent == \"SPELL_CAST_SUCCESS\" and sourceGUID == WeakAuras.myGUID and spellid == 196277 then\n        \n        for _, state in pairs(allstates) do\n            state.show = false\n            state.trigger_count = false\n            state.changed = true\n        end\n        \n        return true\n        \n    end\n    \n    -- Success cast Firebolt and remove 1 progress\n    if subevent == \"SPELL_CAST_SUCCESS\" and allstates[sourceGUID] ~= nil and spellid == 104318 then\n        \n        local state = allstates[sourceGUID]\n        \n        if not state.is_empowered then\n            state.value = state.value - 1\n            state.changed = true\n        end\n        \n        if state.value <= 0 then\n            state.show = false\n            state.trigger_count = false\n            state.changed = true\n        end\n        \n        return true\n        \n    end\n    \nend",
