["custom"] = "aura_env.region.configGroup = \"maelstrom_bar\"\n\naura_env.power = {}\naura_env.power.unmodified = false\naura_env.spells = {\n    \n    --[[SHAMAN]]--\n    [188196] = { --Lightning Bolt\n        power = 8,\n        type = Enum.PowerType.Maelstrom,\n        mods = {\n            {\n                requirement = 2, --1 = any, 2 = all\n                modType = 1, --1 = flat, 2 = percentage\n                modAmount = 2,\n                conditions = { --Type:1 = buff, Type:2 = talent/spell/conduit\n                    {type=2,id=385923}\n                },\n            },\n            {\n                requirement = 2, --1 = any, 2 = all\n                modType = 2, --1 = flat, 2 = percentage\n                modAmount = 1.5,\n                conditions = { --Type:1 = buff, Type:2 = talent/spell/conduit\n                    {type=1,id=410018}\n                },\n            }\n        }\n    },\n    [51505] = { --Lava Burst\n        power = 10,\n        type = Enum.PowerType.Maelstrom,\n        mods = {\n            {\n                requirement = 2, --1 = any, 2 = all\n                modType = 1, --1 = flat, 2 = percentage\n                modAmount = 2,\n                conditions = { --Type:1 = buff, Type:2 = talent/spell/conduit\n                    {type=2,id=385923}\n                },\n            },\n            {\n                requirement = 2, --1 = any, 2 = all\n                modType = 2, --1 = flat, 2 = percentage\n                modAmount = 1.5,\n                conditions = { --Type:1 = buff, Type:2 = talent/spell/conduit\n                    {type=1,id=410018}\n                },\n            }\n        }\n    },\n    [188443] = { --Chain Lightning\n        power = 4,\n        type = Enum.PowerType.Maelstrom,\n    },\n    [210714] = { --Icefury\n        power = 25,\n        type = Enum.PowerType.Maelstrom,\n        mods = {\n            {\n                requirement = 2, --1 = any, 2 = all\n                modType = 2, --1 = flat, 2 = percentage\n                modAmount = 1.5,\n                conditions = { --Type:1 = buff, Type:2 = talent/spell/conduit\n                    {type=1,id=410018}\n                },\n            }\n        }\n    },\n}\naura_env.events = {\n    \"UNIT_SPELLCAST_START\",\n    \"UNIT_SPELLCAST_STOP\" ,\n    \"UNIT_POWER_FREQUENT\",\n    \"OPTIONS\",\n    \"PLAYER_SPECIALIZATION_CHANGED\",\n}\n\n--Accepts a table and returns all indices as keypairs with value true\nlocal function Set(list)\n    local set = {}\n    for _,v in ipairs(list) do set[v] = true end\n    return set\nend\naura_env.events = Set(aura_env.events)\n\naura_env.power.PowerTypes = Enum.PowerType.Maelstrom\n\naura_env.GetCurrentSpecializationID = function()\n    return GetSpecializationInfo(GetSpecialization())\nend\n\naura_env.findAndReplaceOrNew = function(tMain, tNew)\n    for k,v in pairs(tNew) do\n        tMain[v.id] = v\n    end\n    return tMain\nend\n\naura_env.getPowerModifierAmounts = function(spellID)\n    local spell = aura_env.spells[spellID]\n    if not spell or spell and not spell.mods then return 0,nil end\n    local modAmounts = {}\n    local total = spell.power\n    \n    for i,mod in pairs(spell.mods) do\n        local modStates = {}\n        \n        --Check modifier conditions\n        for j,condition in pairs(mod.conditions) do\n            modStates[j] = false\n            \n            if condition.type == 1 then --Buff\n                local aura = C_UnitAuras.GetPlayerAuraBySpellID(condition.id)\n                local endTimeMS = select(5, UnitCastingInfo(\"player\"))\n                \n                if aura and aura.expirationTime and endTimeMS and (endTimeMS/1000) <= aura.expirationTime then\n                    modStates[j] = true\n                end\n            elseif condition.type == 2 then --Talent\n                if IsPlayerSpell(condition.id) then\n                    modStates[j] = true\n                end\n            end\n        end\n        \n        --Check if modifier passes its Any/All requirement\n        local requirements = false\n        if mod.requirement == 1 then --Any\n            for _,modState in pairs(modStates) do\n                if modState then requirements = true end\n            end\n        elseif mod.requirement == 2 then --All\n            requirements = true\n            for _,modState in pairs(modStates) do\n                if not modState then requirements = false end\n            end\n        end\n        \n        --Calculate modifier amounts\n        if requirements then\n            local newTotal = floor(mod.modType == 1 and total+mod.modAmount or mod.modType == 2 and total*mod.modAmount or spell.power)\n            local change = newTotal-total\n            modAmounts[i] = {total=newTotal,change=change}\n            total = newTotal\n        end\n    end\n    \n    --Calculate total power change of all modifiers\n    local totalChange = 0\n    if #modAmounts > 0 then\n        for _,v in pairs(modAmounts) do totalChange = totalChange+v.change end\n    end\n    \n    return totalChange, modAmounts\nend",
