["translateFunc"] = "function(progress, startX, startY, deltaX, deltaY)\n    local radius = 65\n    local radians = 2*math.pi\n    \n    \n    local chunks = aura_env.count\n    local chunkSize = radians / chunks\n    local angleOffset = (aura_env.state.index -1) * chunkSize\n    \n    \n    local angle = 0\n    if aura_env.spinning then\n        if aura_env.state.index == 1 then\n            aura_env.progress = progress\n        end\n    end\n    angle = aura_env.progress * radians\n    angle = angle + angleOffset\n    \n    local x = radius * math.cos(angle)\n    local y = -radius * math.sin(angle)\n    \n    if aura_env.winningIndex > 0 and aura_env.spinning then\n        \n        if aura_env.winningIndex == aura_env.state.index and (y >= -1 and y <= 1) and (x >= 0) then\n            aura_env.spinning = false\n            \n            local key = aura_env.state\n            WeakAuras.ScanEvents(\"PSY_WHEEL_DECIDE_SPIN_STOP\", key.player, key.keystoneFull, key.difficulty) \n            \n            if aura_env.config.announce and IsInGroup() then\n                SendChatMessage(\n                    \"**WHEEL-DECIDE** has spoken! \" .. key.keystone .. \" +\" .. key.difficulty .. \" (\" .. key.player .. \")\",\n                    \"PARTY\"\n                    \n                )\n            end\n        end\n        \n    end\n    \n    \n    return x,y\nend",
